function getDeskriptionWord(numWord)
{
    return "\"" + s[numWord] + "\"(" + trans[numWord] + ")";
}

function ClickFun()
{
    if (state == WAIT)
    {
        leftTime = TIME;
        upDateViewTimer();
        tim = setInterval(tick, 1000);    
        state = PLAY;
        nextButton.disabled = false;
        goButton.disabled = true;
        applyButton.disabled = true;
    }
    else
    {
        var elem = createResult(curWord - 1);// document.createElement("p");
        elem.innerText = (whoSay + 1) + " explained " + getDeskriptionWord(perm[curWord - 1]) + " for " + (forSay + 1);
        resultView.insertBefore(elem, resultView.children[0]);
    }
    if (leftTime > 3)
    {
        word.innerText = nextWord();
    }
    else
    {
        leftTime = 0;
        upDateViewTimer();
        endExplain();
    }
}

function applyCountClick()
{
    var val = inputNumberPlayers.value;
    if (val != "" && inputNumberPlayers.checkValidity(val))
    {
        var cnt = Number.parseInt(val);
        if (whoSay >= cnt || forSay >= cnt)
        {
            forSay = 0;
            whoSay = 1;
            WhoFor.innerText = "Now 1 explaining for 2";
        }
        pCountPlayers.innerText = "Change count players from " + cnt + " to";
        cntPlayer = cnt;       
    }
}

function playClick()
{
    if (audioPlayer.getAttribute("src") != "")
        audioPlayer.play();
}

function createResult(numWord)
{
    var elem = document.createElement("p");
    elem.onclick = function() 
        {
            audioPlayer.setAttribute("src", soundLink[perm[numWord]]);
            audioPlayer.play();    
        }
    return elem;
}

function upDateViewTimer()
{
    viewTimer.innerText = "Time left " + leftTime;
}

function tick()
{
    --leftTime;
    upDateViewTimer();
    if (leftTime == 0)
    {
        var elem = createResult(curWord - 1);
        elem.innerText = (whoSay + 1) + " to late explained " + getDeskriptionWord(perm[curWord - 1]) + "\" for " + (forSay + 1);
        resultView.insertBefore(elem, resultView.children[0]);
        endExplain();        
    }
} 

function endExplain()
{
    state = WAIT;
    word.innerText = "GO!";
    nextButton.disabled = true;
    goButton.disabled = false;
    applyButton.disabled = false;
    clearInterval(tim);
    ++whoSay;
    ++forSay;
    if (whoSay == cntPlayer)
    {
        ++forSay;
        whoSay = 0;
        if (forSay == cntPlayer)
            forSay = 1;
    }
    forSay = forSay % cntPlayer;
    whoForView.innerText = "Now " + (whoSay + 1) + " is explaining for " + (forSay + 1);
    audioPlayer.setAttribute("src", soundLink[perm[curWord - 1]]);
}

function nextWord()
{
    return s[perm[curWord++]];
}

function my_rnd()
{
    var a = Math.random();
    a *= 2000000000;
    return Math.floor(a);
}

